mapper.select:

  		SELECT  buil.* ,area.areaname 'zoneName',cityarea.areaname 'cityName'  from b_building_old buil left join s_area area on buil.zoneId = area.areaid
  		left join s_area cityarea on buil.cityId = cityarea.areaid
  		where
  		buil.deleteFlag = 0 and shelves =1 


      and  buil.cityId = #{cityId}


	      and  buil.zoneId = #{zoneId}


	       and buil.floorType = #{floorType}


	       and buil.decorateSituation = #{decorateSituation}


	       and cityarea.areaname = #{cityName}


	       and area.areaname = #{zoneName}




  		order by buil.updateTime desc
